import { useState, useEffect } from "react";
import { connect, useDispatch } from "react-redux";
import { handleSaveQuestionAnswer } from "../actions/questions";
import { useNavigate } from "react-router-dom";

function QuestionDetail(props) {
  const { authedUser, question, users } = props;
  const dispatch = useDispatch();
  const navigate = useNavigate();
  const [selectOption, setSelectOption] = useState({
    optionOne: "",
    optionTwo: "",
  });

  let optionOneVotes = question.optionOne.votes.length;
  let optionTwoVotes = question.optionTwo.votes.length;
  let totalVotes = optionOneVotes + optionTwoVotes;
  let optionOnePercent = (optionOneVotes / totalVotes) * 100;
  let optionTwoPercent = (optionTwoVotes / totalVotes) * 100;

  useEffect(() => {
    if (authedUser) {
      setSelectOption({
        optionOne: question.optionOne.votes.includes(authedUser)
          ? "optionOne"
          : "",
        optionTwo: question.optionTwo.votes.includes(authedUser)
          ? "optionTwo"
          : "",
      });
    }
  }, [authedUser, question]);

  const handleChange = (e) => {
    setSelectOption({
      ...selectOption,
      [e.target.name]: e.target.value,
    });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    dispatch(
      handleSaveQuestionAnswer({
        authedUser,
        qid: question.id,
        answer: selectOption.optionOne,
      })
    );
    dispatch(
      handleSaveQuestionAnswer({
        authedUser,
        qid: question.id,
        answer: selectOption.optionTwo,
      })
    );
    navigate("/");
  };

  return (
    <div>
      <p>question Detail</p>
    </div>
  );
}

const mapStateToProps = ({ authedUser, questions }) => {
  return {
    questions: Object.keys(questions).map((key) => questions[key]),
    authedUser,
  };
};

export default connect(mapStateToProps)(QuestionDetail);
