import { useState, useEffect } from "react";
import { connect, useDispatch } from "react-redux";
import { handleSaveQuestionAnswer } from "../actions/questions";
import { useNavigate } from "react-router-dom";
import Nav from "./Nav";

function QuestionDetail({ authedUser, questions }) {
  const dispatch = useDispatch();
  const navigate = useNavigate();
  const [selectedAnswer, setSelectedAnswer] = useState({
    optionOne: "",
    optionTwo: "",
  });

  const optionOnePercentage = () => {
    const optionOneVotes = questions.optionOne.votes.length;
    const optionTwoVotes = questions.optionTwo.votes.length;
    const totalVotes = optionOneVotes + optionTwoVotes;
    return (optionOneVotes / totalVotes) * 100;
  };

  const optionTwoPercentage = () => {
    const optionOneVotes = questions.optionOne.votes.length;
    const optionTwoVotes = questions.optionTwo.votes.length;
    const totalVotes = optionOneVotes + optionTwoVotes;
    return (optionTwoVotes / totalVotes) * 100;
  };

  const handleChange = (e) => {
    const value = e.target.value;
    const name = e.target.name;

    if (name === "optionOne") {
      setSelectedAnswer({
        ...selectedAnswer,
        optionOne: value,
      });
    }
    if (name === "optionTwo") {
      setSelectedAnswer({
        ...selectedAnswer,
        optionTwo: value,
      });
    }
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    dispatch(handleSaveQuestionAnswer(questions.id, selectedAnswer));
    navigate("/");
  };

  return (
    <div>
      <div>
        <Nav />
      </div>
    </div>
  );
}

const mapStateToProps = ({ authedUser, questions }) => {
  return {
    questions: Object.keys(questions).map((id) => {
      return questions[id];
    }),
    authedUser,
  };
};

export default connect(mapStateToProps)(QuestionDetail);
