import { connect } from "react-redux";
import { formatQuestion, formatDate } from "../utils/helpers";

const Question = (props) => {
  console.log(props);

  // TODO: redirect to question detail page
  const toDetail = (e, id) => {
    e.preventDefault();
    props.history.push(`/questions/${id}`);
  };

  if (!props.question === null) {
    return <p>This Question doesn't exist</p>;
  }

  const { name, avatar, optionOne, optionTwo, timestamp, detail } =
    props.question;

  return (
    <div className='question'>
      <img src={avatar} alt={`Avatar of ${name}`} className='avatar' />
      <div className='question-info'>
        <div>
          <span>{name}</span>
          {detail && <button onClick={(e) => toDetail(e, detail.id)}></button>}
          <p className='question-option'>{`Would you rather ${optionOne.text.text} or ${optionTwo.text.text}?`}</p>
          <div>Asked at: {formatDate(timestamp)}</div>
        </div>
      </div>
    </div>
  );
};

const mapStateToProps = ({ authedUser, users, questions }, { id }) => {
  const question = questions[id];

  return {
    authedUser,
    question: question
      ? formatQuestion(question, users[question.author], authedUser)
      : null,
  };
};

export default connect(mapStateToProps)(Question);
