import { connect } from "react-redux";
import { formatQuestion, formatDate } from "../utils/helpers";
import { Link, useLocation, useNavigate, useParams } from "react-router-dom";
import Error from "./Error";

const withRouter = (Component) => {
  const ComponentWithRouterProps = (props) => {
    let location = useLocation();
    let navigate = useNavigate();
    let params = useParams();
    return <Component {...props} router={{ location, navigate, params }} />;
  };

  return ComponentWithRouterProps;
};

const Question = (props) => {
  console.log(props);

  const toDetail = (e, id) => {
    e.preventDefault();
    props.history.push(`/questions/${id}`);
  };

  if (!props.question === null) {
    return <Error />;
  }

  const { name, avatar, optionOne, optionTwo, timestamp, detail, id } =
    props.question;

  return (
    <Link to={`/questions/:question_${id}`}>
      <div className='question'>
        <img src={avatar} alt={`Avatar of ${name}`} className='avatar' />
        <div className='question-info'>
          <div>
            <span>{name}</span>
            {detail && (
              <button onClick={(e) => toDetail(e, detail.id)}></button>
            )}
            <p className='question-option'>{`Would you rather ${optionOne.text.text} or ${optionTwo.text.text}?`}</p>
            <div>Asked at: {formatDate(timestamp)}</div>
          </div>
        </div>
      </div>
    </Link>
  );
};

const mapStateToProps = ({ authedUser, users, questions }, { id }) => {
  const question = questions[id];

  return {
    authedUser,
    question: question
      ? formatQuestion(question, users[question.author], authedUser)
      : null,
  };
};

export default withRouter(connect(mapStateToProps)(Question));
