import { useState } from "react";
import { connect } from "react-redux";

const NewQuestion = () => {
  const [optionOneText, setOptionOneText] = useState("");
  const [optionTwoText, setOptionTwoText] = useState("");

  const handleChange = (e) => {
    const { name, value } = e.target;
    if (name === "optionOneText") {
      setOptionOneText(value);
    } else if (name === "optionTwoText") {
      setOptionTwoText(value);
    }
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    console.log("New Question:", optionOne, optionTwo);
    // TODO: add new question to store

    setOptionOneText("");
    setOptionTwoText("");
  };

  return (
    <div>
      <h2>Add New Question</h2>
      <form onSubmit={handleSubmit}>
        <div className='form-group'>
          <label>Would you Rather:</label>
          <input
            type='text'
            name='optionOne'
            value={optionOneText}
            onChange={handleChange}
            className='form-control'
            placeholder='Option One'
          />
          <input
            type='text'
            name='optionTwo'
            value={optionTwoText}
            onChange={handleChange}
            className='form-control'
            placeholder='Option Two'
          />
          <button className='btn' type='submit' disabled={optionOneText === ""}>
            Submit Question
          </button>
        </div>
      </form>
    </div>
  );
};

export default connect()(NewQuestion);
