import { useState, useEffect } from "react";
import { connect, useDispatch, useSelector } from "react-redux";
import { useLocation, useNavigate, useParams } from "react-router-dom";
import { handleSaveQuestionAnswer } from "../actions/questions";
import { formatDate } from "../utils/helpers";
import Nav from "./Nav";

const withRouter = (Component) => {
  const ComponentWithRouterProps = (props) => {
    let location = useLocation();
    let navigate = useNavigate();
    let params = useParams();
    return <Component {...props} router={{ location, navigate, params }} />;
  };

  return ComponentWithRouterProps;
};

function QuestionDetail({ authedUser, users, questions, router }) {
  const question = questions[question.id];

  const dispatch = useDispatch();
  console.log("QuestionDetail props:", {
    authedUser,
    users,
    questions,
    question,
    router,
  });

  useEffect(() => {});

  return (
    <div>
      <div>
        <Nav />
      </div>
      <div>
        <div className='question'>
          <div className='question-info'>
            <div></div>
          </div>
        </div>
      </div>
    </div>
  );
}

const mapStateToProps = ({ authedUser, users, questions }, props) => {
  return {
    authedUser,
    users,
    questions,
    question: Object.keys(questions).map((question) => {
      return questions[question];
    }),

    ...props,
  };
};

export default withRouter(connect(mapStateToProps)(QuestionDetail));
