import { useState, useEffect } from "react";
import { connect, useDispatch } from "react-redux";
import { handleSaveQuestionAnswer } from "../actions/questions";
import { useNavigate } from "react-router-dom";
import Nav from "./Nav";

function QuestionDetail({ authedUser, question, questions }) {
  const dispatch = useDispatch();
  const navigate = useNavigate();
  const [selectedAnswer, setSelectedAnswer] = useState({
    optionOne: "",
    optionTwo: "",
  });

  const optionOnePercentage = () => {
    const optionOneVotes = question.optionOne.votes.length;
    const optionTwoVotes = question.optionTwo.votes.length;
    const totalVotes = optionOneVotes + optionTwoVotes;
    return (optionOneVotes / totalVotes) * 100;
  };

  const optionTwoPercentage = () => {
    const optionOneVotes = question.optionOne.votes.length;
    const optionTwoVotes = question.optionTwo.votes.length;
    const totalVotes = optionOneVotes + optionTwoVotes;
    return (optionTwoVotes / totalVotes) * 100;
  };

  const selectedAnswerValue = {
    authedUser: authedUser.id,
    qid: question.id,
    answer: selectedAnswer,
  };

  const saveSelectedAnswer = () => {
    dispatch(handleSaveQuestionAnswer(selectedAnswerValue));
  };

  useEffect(() => {
    if (question.optionOne.votes.includes(authedUser.id)) {
      setSelectedAnswer({ optionOne: "selected" });
    }
    if (question.optionTwo.votes.includes(authedUser.id)) {
      setSelectedAnswer({ optionTwo: "selected" });
    }
  }, [question, authedUser]);

  return (
    <div>
      <div>
        <Nav />
      </div>
      <h2>Question by: </h2>
      <img
        src={question.author.avatarURL}
        alt={`Avatar of ${question.author.name}`}
        className='avatar'
      />
      {question.author.name}
      <h2>Would you rather?</h2>
    </div>
  );
}

const mapStateToProps = ({ authedUser, questions }) => {
  return {
    questions: Object.keys(questions).map((id) => {
      return questions[id];
    }),
    authedUser,
  };
};

export default connect(mapStateToProps)(QuestionDetail);
