import { useState } from "react";
import { setAuthedUser } from "../actions/authedUser";
import { connect } from "react-redux";
import { useDispatch } from "react-redux";

const Login = (users) => {
  const [selectUser, setSelectUser] = useState("none");
  const dispatch = useDispatch();

  const handleLogin = (e) => {
    e.preventDefault();
    dispatch(setAuthedUser(selectUser));
  };

  return (
    <form className='Login-Form'>
      <h2 className='Login__title'>Select User:</h2>
      <select name='selectUser' onChange={(e) => setSelectUser(e.target.value)}>
        <option value='none' disabled>
          Select User
        </option>
        {users.map((user) => (
          <option key={user.id} value={user.id}>
            {user.name}
          </option>
        ))}
      </select>
      <input
        type='password'
        placeholder='Password'
        className='Login-Form'
        required
      />

      <button className='Login__button' onClick={handleLogin}>
        Login
      </button>
    </form>
  );
};

const mapStateToProps = ({ users }) => {
  return {
    users: Object.keys(users).map(function (key) {
      return users[key];
    }),
  };
};

export default connect(mapStateToProps)(Login);
