import { useState, useEffect } from "react";
import { connect, useDispatch } from "react-redux";
import { handleSaveQuestionAnswer } from "../actions/questions";
import { useNavigate } from "react-router-dom";
import Author from "./Author";

function QuestionDetail({ authedUser, question }) {
  const dispatch = useDispatch();
  const navigate = useNavigate();

  const [selectedAnswer, setSelectedAnswer] = useState({
    optionOne: "",
    optionTwo: "",
  });

  const optionOnePercentage = () => {
    const optionOneVotes = question.optionOne.votes.length;
    const optionTwoVotes = question.optionTwo.votes.length;
    const totalVotes = optionOneVotes + optionTwoVotes;
    return (optionOneVotes / totalVotes) * 100;
  };

  const optionTwoPercentage = () => {
    const optionOneVotes = question.optionOne.votes.length;
    const optionTwoVotes = question.optionTwo.votes.length;
    const totalVotes = optionOneVotes + optionTwoVotes;
    return (optionTwoVotes / totalVotes) * 100;
  };

  const handleAnswer = (e) => {
    const value = e.target.value;
    const name = e.target.name;
    setSelectedAnswer({ ...selectedAnswer, [name]: value });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    dispatch(handleSaveQuestionAnswer(question.id, selectedAnswer));
    setSelectedAnswer({
      optionOne: "",
      optionTwo: "",
    });
    navigate("/");
  };

  return (
    <div className='question-detail'>
      <div className='question-detail-header'>
        <h2>Would you Rather:</h2>
        <Author author={question.author} />
      </div>
      <div className='question-detail-body'>
        <div className='question-detail-body-left'>
          <div className='question-detail-body-left-option'>
            <div className='question-detail-body-left-option-text'>
              <p>{question.optionOne.text}</p>
              <p>{optionOnePercentage()}% of votes</p>
            </div>
            <div className='question-detail-body-left-option-bar'>
              <div
                className='question-detail-body-left-option-bar-fill'
                style={{ width: `${optionOnePercentage()}%` }}
              />
            </div>
          </div>
          <div className='question-detail-body-left-option'>
            <div className='question-detail-body-left-option-text'>
              <p>{question.optionTwo.text}</p>
              <p>{optionTwoPercentage()}% of votes</p>
            </div>
            <div className='question-detail-body-left-option-bar'>
              <div
                className='question-detail-body-left-option-bar-fill'
                style={{ width: `${optionTwoPercentage()}%` }}
              />
            </div>
          </div>
        </div>
        <div className='question-detail-body-right'>
          <div className='question-detail-body-right-answer'>
            <h3>Select an answer to vote on</h3>
            <form onSubmit={handleSubmit}>
              <div className='question-detail-body-right-answer-option'>
                <input
                  type='radio'
                  name='optionOne'
                  value={question.optionOne.text}
                  onChange={handleAnswer}
                  checked={selectedAnswer.optionOne === question.optionOne.text}
                />{" "}
                {question.optionOne.text}
              </div>
              <div className='question-detail-body-right-answer-option'>
                <input
                  type='radio'
                  name='optionTwo'
                  value={question.optionTwo.text}
                  onChange={handleAnswer}
                  checked={selectedAnswer.optionTwo === question.optionTwo.text}
                />{" "}
                {question.optionTwo.text}
              </div>
              <button type='submit'>Submit</button>
            </form>
          </div>
        </div>
      </div>
    </div>
  );
}

const mapStateToProps = ({ authedUser, questions }) => {
  return {
    questions: Object.keys(questions).map((keys) => {
      return questions[keys];
    }),
    authedUser,
  };
};

export default connect(mapStateToProps)(QuestionDetail);
