import { useState } from "react";
import Question from "./Question";
import "./styles/QuestionContainer.css";

function QuestionContainer({ newQuestions, completedQuestions }) {
  const [showNewQuestions, setShowNewQuestions] = useState(true);
  const [showCompletedQuestions, setShowCompletedQuestions] = useState(false);

  const toggleNewQuestions = () => {
    setShowNewQuestions(!showNewQuestions);
  };

  const toggleCompletedQuestions = () => {
    setShowCompletedQuestions(!showCompletedQuestions);
  };

  return (
    <div>
      <div className='container'>
        <div>
          <h3>New Questions</h3>
          <button onClick={toggleNewQuestions}>
            {showNewQuestions ? "Hide" : "Show"}
          </button>
        </div>
        {showNewQuestions && (
          <div className='questions'>
            {newQuestions.map((question) => (
              <Question key={question.id} question={question} />
            ))}
          </div>
        )}
      </div>
      <div className='container'>
        <div>
          <h3>Completed Questions</h3>
          <button onClick={toggleCompletedQuestions}>
            {showCompletedQuestions ? "Hide" : "Show"}
          </button>
        </div>
        {showCompletedQuestions && (
          <div className='questions'>
            {completedQuestions.map((question) => (
              <Question key={question.id} question={question} />
            ))}
          </div>
        )}
      </div>
    </div>
  );
}

export default QuestionContainer;
