import { useState, useEffect } from "react";
import { connect, useDispatch } from "react-redux";
import { handleSaveQuestionAnswer } from "../actions/questions";
import { useNavigate } from "react-router-dom";

function QuestionDetail(props) {
  const dispatch = useDispatch();
  const navigate = useNavigate();

  const { authedUser, question, users } = props;
  const { author, optionOne, optionTwo } = question;
  const authorProfile = users[author].avatarURL;
  const authorName = users[author].name;

  const selectOption = (e) => {
    const answer = e.target.value;
    const qid = question.id;
    dispatch(handleSaveQuestionAnswer({ authedUser, qid, answer }));
    navigate("/");
  };

  return (
    <div>
      <img src={authorProfile} alt={authorName} />
      <h3>{authorName} asks:</h3>
      <p>
        Would you rather {optionOne.text} or {optionTwo.text}?
      </p>
    </div>
  );
}

const mapStateToProps = ({ authedUser, question, users }) => {
  return { authedUser, question, users };
};

export default connect(mapStateToProps)(QuestionDetail);
