import { useState, useEffect } from "react";
import { connect, useDispatch } from "react-redux";
import { handleSaveQuestionAnswer } from "../actions/questions";
import { useNavigate } from "react-router-dom";
import { formatDate } from "../utils/helpers";
import Nav from "./Nav";

function QuestionDetail(props) {
  console.log("QuestionDetail props:", props);
  const { id, authedUser, question, author, user } = props;
  const [answer, setAnswer] = useState("");
  const dispatch = useDispatch();
  const navigate = useNavigate();

  useEffect(() => {
    if (authedUser) {
      setAnswer(user[authedUser].answers[id]);
    }
  }, [authedUser, id, user]);

  const handleSubmit = (e) => {
    e.preventDefault();
    dispatch(handleSaveQuestionAnswer(id, answer));
    navigate("/");
  };

  const handleChange = (e) => {
    setAnswer(e.target.value);
  };

  return (
    <div>
      <div>
        <Nav />
      </div>
      <div>
        <div className='question-detail'>
          <div className='question-detail-header'>
            <div className='question-detail-header-left'>
              <img
                src={author.avatarURL}
                alt={`Avatar of ${author.name}`}
                className='avatar'
              />
              <div className='question-detail-header-left-info'>
                <div className='question-detail-header-left-info-name'>
                  {author.name}
                </div>
                <div className='question-detail-header-left-info-date'>
                  {formatDate(question.timestamp)}
                </div>
              </div>
            </div>
            <div className='question-detail-header-right'>
              <div className='question-detail-header-right-info'>
                <div className='question-detail-header-right-info-name'>
                  Would you rather {question.optionOne.text} or{" "}
                  {question.optionTwo.text}?
                </div>
              </div>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
}

const mapStateToProps = ({ authedUser, questions, users }, props) => {
  const { id } = props.match.params;
  const question = questions[id];
  const author = users[question.author];

  return {
    id,
    authedUser,
    question,
    author,
    user: users[authedUser],
  };
};

export default connect(mapStateToProps)(QuestionDetail);
