import { useState, useEffect } from "react";
import { connect, useDispatch } from "react-redux";
import { handleSaveQuestionAnswer } from "../actions/questions";
import { formatDate } from "../utils/helpers";

import Nav from "./Nav";

function QuestionDetail({ questions, authedUser, users }) {
  const dispatch = useDispatch();

  let questionId = window.location.pathname.split("/")[2];
  let question = questions.find((question) => question.id === questionId);
  let author = users[question.author];
  let optionOne = question.optionOne;
  let optionTwo = question.optionTwo;
  let timestamp = question.timestamp;

  const [answer, setAnswer] = useState("");
  const [error, setError] = useState("");

  useEffect(() => {
    if (authedUser) {
      setAnswer(authedUser.answers[questionId]);
    }
  }, [authedUser, questionId]);

  const handleSubmit = (e) => {
    e.preventDefault();
    if (answer === "") {
      setError("Please select an answer");
    } else {
      dispatch(handleSaveQuestionAnswer(authedUser, questionId, answer));
    }
  };

  const optionOneStats = () => {
    return (
      (optionOne.votes.length /
        (optionOne.votes.length + optionTwo.votes.length)) *
      100
    ).toFixed(2);
  };

  const optionTwoStats = () => {
    return (
      (optionTwo.votes.length /
        (optionOne.votes.length + optionTwo.votes.length)) *
      100
    ).toFixed(2);
  };

  return (
    <div>
      <div>
        <Nav />
      </div>
      <div className='container'>
        <div>
          <img src={author.avatarURL} alt={author.name} />
          <h4>{author.name}</h4>
          <h5>Asked at: {formatDate(timestamp)}</h5>
        </div>
        <form onChange={handleSubmit}>
          <div className='form-group'>
            <label>Would you rather:</label>
            <div className='form-check'>
              <input
                type='radio'
                name='option'
                value='optionOne'
                checked={answer === "optionOne"}
                onChange={(e) => setAnswer(e.target.value)}
              />
              <label>{optionOne.text}</label>
            </div>
            <div>
              <input
                type='radio'
                name='option'
                value='optionTwo'
                checked={answer === "optionTwo"}
                onChange={(e) => setAnswer(e.target.value)}
              />
              <label>{optionTwo.text}</label>
            </div>
            <button className='btn' type='submit'>
              Submit
            </button>
          </div>
        </form>
      </div>
    </div>
  );
}

const mapStateToProps = ({ authedUser, users, questions }) => {
  return {
    authedUser,
    users,
    questions: Object.values(questions),
  };
};

export default connect(mapStateToProps)(QuestionDetail);
