import { useState, useEffect } from "react";
import { connect, useDispatch } from "react-redux";
import { handleSaveQuestionAnswer } from "../actions/questions";
import { formatQuestion } from "../utils/helpers";
import { useNavigate } from "react-router-dom";
import Nav from "./Nav";

function QuestionDetail(props) {
  console.log("QuestionDetail props:", props);
  const { authedUser, question } = props;
  const dispatch = useDispatch();
  const navigate = useNavigate();
  const [answer, setAnswer] = useState("");
  const [error, setError] = useState("");

  useEffect(() => {
    if (question === null) {
      navigate("/error");
    }

    if (authedUser) {
      setAnswer(authedUser.answers[question.id]);
    }
  }, [question, authedUser, navigate]);

  const handleChange = (e) => {
    setAnswer(e.target.value);
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    if (!authedUser) {
      setError("Please login to answer questions");
      return;
    }
    if (!answer) {
      setError("Please select an answer");
      return;
    }
    dispatch(handleSaveQuestionAnswer(question.id, answer));
    navigate("/");
  };

  if (question === null) {
    return <div>Loading...</div>;
  }

  return (
    <div>
      <div>
        <Nav />
      </div>
      <div>
        <form>
          <div>
            <h3>{props.question.optionOne.text}</h3>
            <div>
              <input type='radio' name='option' value='optionOne' />
            </div>
            <h3>{props.question.optionTwo.text}</h3>
            <div>
              <input type='radio' name='option' value='optionTwo' />
            </div>
          </div>
        </form>
      </div>
    </div>
  );
}

const mapStateToProps = ({ authedUser, questions, users }, props) => {
  const question = questions[props.id];
  return {
    authedUser,
    question: question
      ? formatQuestion(question, users[question.author], authedUser)
      : null,
  };
};

export default connect(mapStateToProps)(QuestionDetail);
