import { useState, useEffect } from "react";
import { connect, useDispatch } from "react-redux";
import { handleSaveQuestionAnswer } from "../actions/questions";
import { useNavigate } from "react-router-dom";

function QuestionDetail(props) {
  const { authedUser, question } = props;
  const dispatch = useDispatch();
  const navigate = useNavigate();
  const [selectOption, setSelectOption] = useState({
    optionOne: "",
    optionTwo: "",
  });

  useEffect(() => {
    if (authedUser) {
      setSelectOption({
        optionOne:
          authedUser.answers[question.id] === "optionOne" ? "selected" : "",
        optionTwo:
          authedUser.answers[question.id] === "optionTwo" ? "selected" : "",
      });
    }
  }, [authedUser, question]);

  const handleChange = (e) => {
    setSelectOption({
      ...selectOption,
      [e.target.name]: e.target.value,
    });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    dispatch(
      handleSaveQuestionAnswer({
        authedUser,
        qid: question.id,
        answer: selectOption.optionOne,
      })
    );
    dispatch(
      handleSaveQuestionAnswer({
        authedUser,
        qid: question.id,
        answer: selectOption.optionTwo,
      })
    );
    navigate("/");
  };

  return (
    <div>
      <p>question Detail</p>
    </div>
  );
}

const mapStateToProps = ({ authedUser, questions }) => {
  return {
    questions: Object.keys(questions).map((key) => questions[key]),
    authedUser,
  };
};

export default connect(mapStateToProps)(QuestionDetail);
