import { useState, useEffect } from "react";
import { connect, useDispatch } from "react-redux";
import { handleSaveQuestionAnswer } from "../actions/questions";
import { useNavigate } from "react-router-dom";
import Nav from "./Nav";

function QuestionDetail({ authedUser, questions }) {
  const dispatch = useDispatch();
  const navigate = useNavigate();
  const [selectedAnswer, setSelectedAnswer] = useState({
    optionOne: "",
    optionTwo: "",
  });

  useEffect(() => {
    if (authedUser) {
      const question = questions[authedUser];
      if (question) {
        setSelectedAnswer({
          optionOne: question.optionOne,
          optionTwo: question.optionTwo,
        });
      }
    }
  }, [authedUser, questions]);

  const handleChange = (e) => {
    const value = e.target.value;
    const name = e.target.name;

    if (name === "optionOne") {
      setSelectedAnswer({ ...selectedAnswer, optionOne: value });
      dispatch(
        handleSaveQuestionAnswer(authedUser, questions[authedUser].id, value)
      );
    }
    if (name === "optionTwo") {
      setSelectedAnswer({ ...selectedAnswer, optionTwo: value });
      dispatch(
        handleSaveQuestionAnswer(authedUser, questions[authedUser].id, value)
      );
    }
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    navigate("/");
  };

  const optionOnePercentage = () => {
    const optionOneVotes = questions.optionOne.votes.length;
    const optionTwoVotes = questions.optionTwo.votes.length;
    const totalVotes = optionOneVotes + optionTwoVotes;
    return (optionOneVotes / totalVotes) * 100;
  };

  const optionTwoPercentage = () => {
    const optionOneVotes = questions.optionOne.votes.length;
    const optionTwoVotes = questions.optionTwo.votes.length;
    const totalVotes = optionOneVotes + optionTwoVotes;
    return (optionTwoVotes / totalVotes) * 100;
  };

  return (
    <div>
      <div>
        <Nav />
      </div>
      <h2>Would you rather: </h2>
      <div className='form-group'>
        <label>
          <input
            type='radio'
            name='option'
            value='optionOne'
            checked={selectedAnswer.optionOne === questions.optionOne.text}
            onChange={handleChange}
          />
          {questions.optionOne.text}
        </label>
      </div>
    </div>
  );
}

const mapStateToProps = ({ authedUser, questions }) => {
  return {
    questions: Object.keys(questions).map((id) => {
      return questions[id];
    }),
    authedUser,
  };
};

export default connect(mapStateToProps)(QuestionDetail);
