import { useState, useEffect } from "react";
import { connect, useDispatch } from "react-redux";
import { handleSaveQuestionAnswer } from "../actions/questions";
import { formatDate } from "../utils/helpers";

import Nav from "./Nav";

function QuestionDetail({ questions, authedUser, users }) {
  const dispatch = useDispatch();

  let questionId = window.location.pathname.split("/")[2];
  let question = questions.find((question) => question.id === questionId);
  let author = users[question.author];
  let optionOne = question.optionOne;
  let optionTwo = question.optionTwo;
  let timestamp = question.timestamp;

  const [selectOption, setSelectOption] = useState({
    optionOne: "none",
    optionTwo: "none",
  });

  useEffect(() => {
    if (question.optionOne.votes.includes(authedUser)) {
      setSelectOption({
        optionOne: "selected",
      });
    }
    if (question.optionTwo.votes.includes(authedUser)) {
      setSelectOption({
        optionTwo: "selected",
      });
    }
  }, [question]);

  const selectedOne = {
    authedUser: authedUser,
    qid: questionId,
    answer: "optionOne",
  };

  const saveSelectOne = () => {
    dispatch(handleSaveQuestionAnswer(selectedOne));
  };

  const selectedTwo = {
    authedUser: authedUser,
    qid: questionId,
    answer: "optionTwo",
  };

  const saveSelectTwo = () => {
    dispatch(handleSaveQuestionAnswer(selectedTwo));
  };

  const optionOneStats = () => {
    return (
      (optionOne.votes.length /
        (optionOne.votes.length + optionTwo.votes.length)) *
      100
    ).toFixed(2);
  };

  const optionTwoStats = () => {
    return (
      (optionTwo.votes.length /
        (optionOne.votes.length + optionTwo.votes.length)) *
      100
    ).toFixed(2);
  };

  return (
    <div>
      <div>
        <Nav />
      </div>
      <div className='container'>
        <div>
          <img src={author.avatarURL} alt={author.name} />
          <h4>{author.name}</h4>
          <h5>Asked at: {formatDate(timestamp)}</h5>
        </div>
        <form>
          <div className='form-group'>
            <label>Would you rather:</label>
          </div>
        </form>
      </div>
    </div>
  );
}

const mapStateToProps = ({ authedUser, users, questions }) => {
  return {
    authedUser,
    users,
    questions: Object.values(questions),
  };
};

export default connect(mapStateToProps)(QuestionDetail);
