import { useState, useEffect } from "react";
import { connect, useDispatch, useSelector } from "react-redux";
import { handleSaveQuestionAnswer } from "../actions/questions";
import { formatDate } from "../utils/helpers";

import Nav from "./Nav";

function QuestionDetail({ questions, authedUser, users }) {
  const dispatch = useDispatch();

  let questionId = window.location.pathname.split("/")[2];
  let question = questions.find((question) => question.id === questionId);
  let author = users[question.author];
  let optionOne = question.optionOne;
  let optionTwo = question.optionTwo;
  let timestamp = question.timestamp;

  const [selectOption, setSelectOption] = useState({
    optionOne: "none",
    optionTwo: "none",
  });

  useEffect(() => {
    if (optionOne.votes.includes(authedUser)) {
      setSelectOption({
        optionOne: "selected",
      });
    }
    if (optionTwo.votes.includes(authedUser)) {
      setSelectOption({
        optionTwo: "selected",
      });
    }
  }, [optionOne, optionTwo, authedUser]);

  const selectedOne = {
    authedUser: authedUser,
    qid: questionId,
    answer: "optionOne",
  };

  const selectedTwo = {
    authedUser: authedUser,
    qid: questionId,
    answer: "optionTwo",
  };

  const optionOneStats = () => {
    return (
      (optionOne.votes.length /
        (optionOne.votes.length + optionTwo.votes.length)) *
      100
    ).toFixed(2);
  };

  const optionTwoStats = () => {
    return (
      (optionTwo.votes.length /
        (optionOne.votes.length + optionTwo.votes.length)) *
      100
    ).toFixed(2);
  };

  const handleChange = (e) => {
    const value = e.target.value;
    const name = e.target.name;

    if (name === "optionOne") {
      setSelectOption({ ...selectOption, optionOne: value });
    }
    if (name === "optionTwo") {
      setSelectOption({ ...selectOption, optionTwo: value });
    }
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    if (selectOption.optionOne === "optionOne") {
      dispatch(handleSaveQuestionAnswer(selectedOne));
    }
    if (selectOption.optionOne === "optionTwo") {
      dispatch(handleSaveQuestionAnswer(selectedTwo));
    }
  };

  return (
    <div>
      <div>
        <Nav />
      </div>
      <div className='question-detail'>
        <div className='question-detail-header'>
          <div className='question-detail-header-left'>
            <img
              src={author.avatarURL}
              alt={`Avatar of ${author.name}`}
              className='avatar'
            />
          </div>
          <span>Would you rather:</span>
        </div>
        <div className='question-detail-body'>
          <form onSubmit={handleSubmit}>
            <div classname='question-group'>
              <input
                type='radio'
                name='optionOne'
                value='optionOne'
                onChange={handleChange}
                checked={selectOption.optionOne === "optionOne"}
              />
              <label>{optionOne.text}</label>
            </div>
          </form>
        </div>
      </div>
    </div>
  );
}

const mapStateToProps = ({ authedUser, users, questions }) => {
  return {
    authedUser,
    users,
    questions: Object.values(questions),
  };
};

export default connect(mapStateToProps)(QuestionDetail);
