import { useState, useEffect } from "react";
import { connect, useDispatch } from "react-redux";
import { handleSaveQuestionAnswer } from "../actions/questions";
import { useLocation, useNavigate, useParams } from "react-router-dom";
import { formatDate } from "../utils/helpers";

const withRouter = (Component) => {
  const ComponentWithRouterProps = (props) => {
    let location = useLocation();
    let navigate = useNavigate();
    let params = useParams();
    return <Component {...props} router={{ location, navigate, params }} />;
  };

  return ComponentWithRouterProps;
};

function QuestionDetail(props) {
  const { authedUser, question, users } = props;
  const [answer, setAnswer] = useState(null);
  const { name, avatar, optionOne, optionTwo, timestamp, detail, id } =
    question;
  const dispatch = useDispatch();

  useEffect(() => {
    if (authedUser) {
      setAnswer(authedUser.answers[question.id]);
    }
  }, [authedUser, question]);

  const handleSubmit = (e) => {
    e.preventDefault();
    props.dispatch(handleSaveQuestionAnswer(question.id, answer));
  };

  const handleChange = (e) => {
    setAnswer(e.target.value);
  };

  if (!question) {
    return <p>This Question doesn't exist</p>;
  }

  return (
    <div className='question'>
      <img src={avatar} alt={`Avatar of ${name}`} className='avatar' />
      <div className='question-info'>
        <div>
          <span>{name}</span>
          <p className='question-option'>{`Would you rather ${optionOne.text.text} or ${optionTwo.text.text}?`}</p>
          <div>Asked at: {formatDate(timestamp)}</div>
        </div>
        <div className='question-answer'>
          <form onSubmit={handleSubmit}>
            <div className='question-answer-option'>
              <input
                type='radio'
                name='answer'
                value='optionOne'
                checked={answer === "optionOne"}
                onChange={handleChange}
              />
              <label>{optionOne.text.text}</label>
            </div>
            <div className='question-answer-option'>
              <input
                type='radio'
                name='answer'
                value='optionTwo'
                checked={answer === "optionTwo"}
                onChange={handleChange}
              />
              <label>{optionTwo.text.text}</label>
            </div>
            <button type='submit'>Submit</button>
          </form>
        </div>
      </div>
    </div>
  );
}

const mapStateToProps = ({ users, authedUser }, { question }) => {
  return { authedUser, question, users };
};

export default withRouter(connect(mapStateToProps)(QuestionDetail));
