}

const mapStateToProps = (state, ownProps) => {
  const { authedUser, users } = state;
  const { id } = ownProps.match.params;
  const question = state.questions[id];

  return {
    authedUser,
    users,
    question,
  };
};

export default connect(mapStateToProps)(QuestionDetail);
