import { useState, useEffect } from "react";
import { connect, useDispatch } from "react-redux";
import { handleSaveQuestionAnswer } from "../actions/questions";

function QuestionDetail({ question, authedUser }) {
  const dispatch = useDispatch();
  const [option, setOption] = useState({
    optionOne: "",
    optionTwo: "",
  });

  const statOne = () => {
    return (
      (question.optionOne.votes.length /
        (question.optionOne.votes.length + question.optionTwo.votes.length)) *
      100
    ).toFixed(2);
  };

  const statTwo = () => {
    return (
      (question.optionTwo.votes.length /
        (question.optionOne.votes.length + question.optionTwo.votes.length)) *
      100
    ).toFixed(2);
  };

  const answerOne = {
    authedUser: authedUser.id,
    qid: question.id,
    answer: "optionOne",
  };

  const saveAnswerOne = () => {
    dispatch(handleSaveQuestionAnswer(answerOne));
  };

  const answerTwo = {
    authedUser: authedUser.id,
    qid: question.id,
    answer: "optionTwo",
  };

  const saveAnswerTwo = () => {
    dispatch(handleSaveQuestionAnswer(answerTwo));
  };

  useEffect(() => {
    if (question.optionOne.votes.includes(authedUser.id)) {
      setOption({
        optionOne: "selected",
      });
      if (question.optionTwo.votes.includes(authedUser.id)) {
        setOption({
          optionTwo: "selected",
        });
      }
    }
  }, [question, authedUser]);

  return <div></div>;
}

const mapStateToProps = ({ questions, authedUser }) => {
  return {
    questions: Object.keys(questions).map((key) => questions[key]),
    authedUser,
  };
};

export default connect(mapStateToProps)(QuestionDetail);
