import { useState, useEffect } from "react";

import { useDispatch, useSelector } from "react-redux";
import { handleSaveQuestionAnswer } from "../actions/questions";
import Nav from "./Nav";

function QuestionDetail() {
  const [option, setOption] = useState("");
  const dispatch = useDispatch();
  const { question } = useSelector((state) => state.questions);
  const { authedUser } = useSelector((state) => state.authedUser);

  console.log(question);

  useEffect(() => {
    if (authedUser) {
      setOption(authedUser);
    }
  }, [authedUser]);

  const handleSubmit = (e) => {
    e.preventDefault();
    dispatch(handleSaveQuestionAnswer(question.id, option));
  };

  return (
    <div className='container'>
      <Nav />
      <div className='question-detail'>
        <div className='question-detail__question'>
          <h2>{question.text}</h2>
        </div>
        <div className='question-detail__answers'>
          <form onSubmit={handleSubmit}>
            <div className='question-detail__answers__option'>
              <input
                type='radio'
                name='option'
                value='optionOne'
                checked={option === "optionOne"}
                onChange={(e) => setOption(e.target.value)}
              />
              <label>{question.optionOne.text}</label>
            </div>
            <div className='question-detail__answers__option'>
              <input
                type='radio'
                name='option'
                value='optionTwo'
                checked={option === "optionTwo"}
                onChange={(e) => setOption(e.target.value)}
              />
              <label>{question.optionTwo.text}</label>
            </div>
            <button type='submit' className='btn'>
              Submit
            </button>
          </form>
        </div>
      </div>
    </div>
  );
}

export default QuestionDetail;
