const Author = ({ name, avatarURL }) => {
  return (
    <div className='author'>
      <img src={avatarURL} alt={`Avatar of ${name}`} className='avatar' />
      <div className='author-info'>
        <div>
          <span>{name}</span>
        </div>
      </div>
    </div>
  );
};

export default Author;
