import { useState, useEffect } from "react";
import { connect, useDispatch } from "react-redux";
import { handleSaveQuestionAnswer } from "../actions/questions";
import { useNavigate } from "react-router-dom";

function QuestionDetail(props) {
  const { authedUser, question, users } = props;
  const dispatch = useDispatch();
  const navigate = useNavigate();
  const [selectOption, setSelectOption] = useState({
    optionOne: "",
    optionTwo: "",
  });

  let author = users[question.author];
  let optionOneVotes = question.optionOne.votes.length;
  let optionTwoVotes = question.optionTwo.votes.length;
  let totalVotes = optionOneVotes + optionTwoVotes;
  let optionOnePercent = (optionOneVotes / totalVotes) * 100;
  let optionTwoPercent = (optionTwoVotes / totalVotes) * 100;

  useEffect(() => {
    if (authedUser) {
      setSelectOption({
        optionOne: question.optionOne.votes.includes(authedUser)
          ? "optionOne"
          : "",
        optionTwo: question.optionTwo.votes.includes(authedUser)
          ? "optionTwo"
          : "",
      });
    }
  }, [authedUser, question]);

  const handleChange = (e) => {
    setSelectOption({
      ...selectOption,
      [e.target.name]: e.target.value,
    });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    dispatch(
      handleSaveQuestionAnswer({
        authedUser,
        qid: question.id,
        answer: selectOption.optionOne,
      })
    );
    dispatch(
      handleSaveQuestionAnswer({
        authedUser,
        qid: question.id,
        answer: selectOption.optionTwo,
      })
    );
    navigate("/");
  };

  return (
    <div>
      <h3>{author} asks:</h3>
      <p>
        Would you rather {question.optionOne.text} or {question.optionTwo.text}?
      </p>
      <form onSubmit={handleSubmit}>
        <div className='form-group'>
          <label>
            <input
              type='radio'
              name='optionOne'
              value='optionOne'
              checked={selectOption.optionOne === "optionOne"}
              onChange={handleChange}
            />
            {question.optionOne.text}
          </label>
        </div>
        <div className='form-group'>
          <label>
            <input
              type='radio'
              name='optionTwo'
              value='optionTwo'
              checked={selectOption.optionTwo === "optionTwo"}
              onChange={handleChange}
            />
            {question.optionTwo.text}
          </label>
        </div>
        <button type='submit' className='btn btn-primary'>
          Submit
        </button>
      </form>
    </div>
  );
}

const mapStateToProps = ({ users, authedUser }, { question }) => {
  return { authedUser, question, users };
};

export default connect(mapStateToProps)(QuestionDetail);
