import { connect } from "react-redux";
import { useLocation } from "react-router-dom";
import Nav from "./Nav";
import QuestionDetail from "./QuestionDetail";
import Error from "./Error";

function QuestionPage({ questions }) {
  const location = useLocation();
  const questionView = questions.filter(
    (question) => question.id === location.pathname.slice(6)
  );

  const [questionViewObj] = questionView;

  return (
    <div>
      {questionViewObj ? (
        <div>
          <Nav />
          <QuestionDetail question={questionViewObj} />
        </div>
      ) : (
        <Error />
      )}
    </div>
  );
}

const mapStateToProps = ({ questions }) => {
  return {
    questions: Object.keys(questions).map((key) => questions[key]),
  };
};

export default connect(mapStateToProps)(QuestionPage);
