import { useState } from "react";
import { connect, useDispatch } from "react-redux";
import { handleAddQuestion } from "../actions/questions";

const NewQuestion = () => {
  const [optionOneText, setOptionOneText] = useState("");
  const [optionTwoText, setOptionTwoText] = useState("");
  const dispatch = useDispatch();

  const question = {
    optionOneText,
    optionTwoText,
    author: authedUser.id,
  };

  const handleChange = (e) => {
    const value = e.target.value;
    const name = e.target.name;
    if (name === "optionOneText") {
      setOptionOneText(value);
    }
    if (name === "optionTwoText") {
      setOptionTwoText(value);
    }
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    console.log("The New Question:", optionOneText, optionTwoText);
    dispatch(handleAddQuestion(question));
  };

  return (
    <div>
      <h2>Add New Question</h2>
      <form onSubmit={handleSubmit}>
        <div className='form-group'>
          <label>Would you Rather:</label>
          <input
            type='text'
            name='optionOneText'
            value={question.optionOneText}
            onChange={handleChange}
            className='form-control'
            placeholder='Option One'
          />
          <label>or</label>
          <input
            type='text'
            name='optionTwoText'
            value={question.optionTwoText}
            onChange={handleChange}
            className='form-control'
            placeholder='Option Two'
          />
          <button
            className='btn'
            type='submit'
            disabled={
              question.optionOneText === "" || question.optionTwoText === ""
            }
          >
            Submit Question
          </button>
        </div>
      </form>
    </div>
  );
};

const mapStateToProps = ({ authedUser }) => {
  return {
    authedUser,
  };
};

export default connect(mapStateToProps)(NewQuestion);
