import { connect } from "react-redux";
import { useEffect, useState } from "react";
import NewQuestionContainer from "./NewQuestionContainer";
import CompletedQuestionContainer from "./CompletedQuestionContainer";
import Nav from "./Nav";

const Dashboard = ({ authedUser, questions }) => {
  const [newQuestions, setNewQuestions] = useState([]);
  const [completedQuestions, setCompletedQuestions] = useState([]);

  useEffect(() => {
    setCompletedQuestions(
      questions
        .filter(
          (question) =>
            question.optionOne.votes.includes(authedUser) ||
            question.optionTwo.votes.includes(authedUser)
        )
        .sort((a, b) => b.timestamp - a.timestamp)
    );

    setNewQuestions(
      questions
        .filter(
          (question) =>
            !question.optionOne.votes.includes(authedUser) &&
            !question.optionTwo.votes.includes(authedUser)
        )
        .sort((a, b) => b.timestamp - a.timestamp)
    );
  }, [authedUser, questions]);

  const [showNewQuestions, setShowNewQuestions] = useState(true);
  const [showCompletedQuestions, setShowCompletedQuestions] = useState(false);

  const toggleNewQuestions = () => {
    setShowNewQuestions(!showNewQuestions);
  };

  const toggleCompletedQuestions = () => {
    setShowCompletedQuestions(!showCompletedQuestions);
  };

  return (
    <div>
      <div>
        <Nav />
      </div>
      <div>
        <NewQuestionContainer newQuestions={newQuestions} />

        <div className='toggle-container'>
          <button
            className={
              showNewQuestions ? "toggle-button-active" : "toggle-button"
            }
            onClick={toggleNewQuestions}
          >
            {" "}
            New Questions
          </button>
        </div>
        <CompletedQuestionContainer completedQuestions={completedQuestions} />
      </div>
    </div>
  );
};

const mapStateToProps = ({ questions, authedUser }) => ({
  questions: Object.values(questions),
  authedUser,
});

export default connect(mapStateToProps)(Dashboard);
