import e from "express";
import { useState, useEffect } from "react";
import { connect, useDispatch } from "react-redux";
import { useParams } from "react-router-dom";
import { handleSaveQuestionAnswer } from "../actions/questions";
import { formatDate } from "../utils/helpers";
import Nav from "./Nav";

function QuestionDetail({ questions, authedUser, users }) {
  const dispatch = useDispatch();
  const { question_id } = useParams();
  let question = questions.find((question) => question.id === question_id);
  let author = users[question.author];
  let timestamp = question.timestamp;

  const [selectOption, setSelectOption] = useState({
    optionOne: "",
    optionTwo: "",
  });

  useEffect(() => {
    if (question) {
      setSelectOption({
        optionOne: question.optionOne.votes.includes(authedUser)
          ? "selected"
          : "",
        optionTwo: question.optionTwo.votes.includes(authedUser)
          ? "selected"
          : "",
      });
    }
  }, [question, authedUser]);

  const optionOneStats = () => {
    return (
      (question.optionOne.votes.length /
        (question.optionOne.votes.length + question.optionTwo.votes.length)) *
      100
    ).toFixed(2);
  };

  const optionTwoStats = () => {
    return (
      (question.optionTwo.votes.length /
        (question.optionOne.votes.length + question.optionTwo.votes.length)) *
      100
    ).toFixed(2);
  };

  const displayStats = () => {
    e.preventDefault();
    return (
      <div className='stats'>
        <div className='stats__option'>
          <div className='stats__option__text'>
            <h3>Would you rather</h3>
            <p>{question.optionOne.text}</p>
            <p>{optionOneStats()}% of people picked this</p>
          </div>
          <div className='stats__option__progress'>
            <div
              className={`stats__option__progress__bar ${
                selectOption.optionOne === "selected" ? "selected" : ""
              }`}
              style={{
                width: `${optionOneStats()}%`,
              }}
            ></div>
          </div>
        </div>
        <div className='stats__option'>
          <div className='stats__option__text'>
            <h3>Would you rather</h3>
            <p>{question.optionTwo.text}</p>
            <p>{optionTwoStats()}% of people picked this</p>
          </div>
          <div className='stats__option__progress'>
            <div
              className={`stats__option__progress__bar ${
                selectOption.optionTwo === "selected" ? "selected" : ""
              }`}
              style={{
                width: `${optionTwoStats()}%`,
              }}
            ></div>
          </div>
        </div>
      </div>
    );
  };

  const selectedOption = {
    authedUser: authedUser,
    qid: question_id,
    answer: selectOption.optionOne === "selected" ? "optionOne" : "optionTwo",
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    dispatch(handleSaveQuestionAnswer(selectedOption));
  };

  return (
    <div>
      <div>
        <Nav />
      </div>
      <div className='container'>
        <div>
          <img src={author.avatarURL} alt={author.name} />
          <h4>{author.name}</h4>
          <h5>Asked at: {formatDate(timestamp)}</h5>
        </div>
        <form onSubmit={handleSubmit}>
          <div className='form-group'>
            <label>Would you rather:</label>
            <div className='form-check'>
              <input
                type='radio'
                name='option'
                value='optionOne'
                checked={selectOption.optionOne === "selected"}
                onChange={(e) => setSelectOption({ optionOne: "selected" })}
              />
              <label>{question.optionOne.text}</label>
            </div>
            <div>
              <input
                type='radio'
                name='option'
                value='optionTwo'
                checked={selectOption.optionTwo === "selected"}
                onChange={(e) => setSelectOption({ optionTwo: "selected" })}
              />
              <label>{question.optionTwo.text}</label>
            </div>
            <button className='btn' type='submit'>
              Submit
            </button>
          </div>
        </form>
      </div>
    </div>
  );
}

const mapStateToProps = ({ authedUser, users, questions }) => {
  return {
    authedUser,
    users,
    questions: Object.keys(questions).map((key) => questions[key]),
  };
};

export default connect(mapStateToProps)(QuestionDetail);
